import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { motion, AnimatePresence } from 'framer-motion';

const BACKEND_URL = process.env.NODE_ENV === 'production' 
  ? window.location.origin 
  : 'http://localhost:5000';

function App() {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [messages, setMessages] = useState([]);
  const [textInput, setTextInput] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  useEffect(() => {
    const newSocket = io(BACKEND_URL, {
      transports: ['websocket', 'polling'],
      timeout: 10000,
    });

    // Connection handlers
    newSocket.on('connect', () => {
      console.log('✅ Connected to VEGA');
      setIsConnected(true);
      setConnectionStatus('Connected');
      addMessage('system', '✨ VEGA is now online and ready to assist you!');
    });

    newSocket.on('disconnect', () => {
      console.log('❌ Disconnected');
      setIsConnected(false);
      setIsListening(false);
      setIsSpeaking(false);
      setConnectionStatus('Disconnected');
    });

    // Voice interaction handlers
    newSocket.on('listening_started', () => {
      setIsListening(true);
      addMessage('system', '🎤 Listening... speak your command');
    });

    newSocket.on('listening_stopped', () => {
      setIsListening(false);
    });

    newSocket.on('user_spoke', (data) => {
      addMessage('user', data.text, data.timestamp);
    });

    newSocket.on('assistant_speaking', (data) => {
      addMessage('assistant', data.text, data.timestamp);
      setIsSpeaking(true);
    });

    newSocket.on('assistant_finished_speaking', () => {
      setIsSpeaking(false);
    });

    newSocket.on('command_executed', (data) => {
      console.log('Command executed:', data);
    });

    newSocket.on('error', (data) => {
      addMessage('system', `❌ Error: ${data.message}`);
      setIsListening(false);
      setIsSpeaking(false);
    });

    newSocket.on('no_speech_detected', () => {
      addMessage('system', '⚠️ No speech detected. Please try again.');
      setIsListening(false);
    });

    setSocket(newSocket);
    return () => newSocket.close();
  }, []);

  const addMessage = (type, text, timestamp = null) => {
    const message = {
      type,
      text,
      timestamp: timestamp || new Date().toISOString(),
      id: Date.now() + Math.random()
    };
    setMessages(prev => [...prev, message]);
  };

  const toggleListening = () => {
    if (!socket || !isConnected) return;
    if (isListening) {
      socket.emit('stop_listening');
    } else {
      socket.emit('start_listening');
    }
  };

  const sendTextCommand = () => {
    if (!textInput.trim() || !socket || !isConnected) return;

    socket.emit('send_text_command', { command: textInput });
    setTextInput('');
  };

  const initializeVega = () => {
    if (socket && isConnected) {
      socket.emit('initialize_vega');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-blue-800 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0">
        <div className="absolute inset-0 bg-gradient-to-br from-blue-600/20 via-purple-600/20 to-cyan-600/20"></div>
        <AnimatedBackground />
      </div>

      {/* Header */}
      <header className="relative z-10 text-center py-8 bg-black/20 backdrop-blur-lg border-b border-white/10">
        <motion.h1
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, type: "spring" }}
          className="text-6xl font-bold bg-gradient-to-r from-cyan-400 via-blue-400 to-purple-400 bg-clip-text text-transparent"
        >
          V.E.G.A
        </motion.h1>
        <motion.p
          initial={{ y: -30, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.2, type: "spring" }}
          className="text-xl text-gray-300 mt-2"
        >
          Voice-Enabled Genius Assistant
        </motion.p>
        <div className={`inline-flex items-center gap-2 mt-4 px-4 py-2 rounded-full backdrop-blur-md ${
          isConnected 
            ? 'bg-green-500/20 text-green-400 border border-green-400/30' 
            : 'bg-red-500/20 text-red-400 border border-red-400/30'
        }`}>
          <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-400' : 'bg-red-400'}`}></div>
          {connectionStatus}
        </div>
      </header>

      {/* Main Interface */}
      <div className="relative z-10 flex flex-col lg:flex-row min-h-[calc(100vh-200px)] p-6 gap-8">

        {/* Voice Visualizer Section */}
        <div className="flex-1 flex flex-col items-center justify-center">
          <VoiceVisualizer 
            isListening={isListening} 
            isSpeaking={isSpeaking} 
            isConnected={isConnected}
          />

          {/* Microphone Control */}
          <div className="mt-8 text-center">
            <motion.button
              className={`
                w-24 h-24 rounded-full border-4 backdrop-blur-lg font-bold text-2xl
                transition-all duration-300 shadow-2xl
                ${isListening 
                  ? 'bg-red-500/80 border-red-400 text-white shadow-red-500/50' 
                  : isSpeaking 
                  ? 'bg-blue-500/80 border-blue-400 text-white shadow-blue-500/50'
                  : 'bg-white/20 border-white/30 text-white hover:bg-white/30 shadow-white/20'
                }
              `}
              whileHover={{ scale: isConnected ? 1.1 : 1 }}
              whileTap={{ scale: isConnected ? 0.9 : 1 }}
              onClick={toggleListening}
              disabled={!isConnected || isSpeaking}
            >
              {isListening ? '🛑' : '🎤'}
            </motion.button>

            <div className="mt-4 px-6 py-3 rounded-2xl bg-black/40 backdrop-blur-lg border border-white/20">
              <p className="text-white/90 font-medium">
                {isSpeaking ? '🤖 VEGA is responding...' :
                 isListening ? '🎤 Listening for your command...' :
                 isConnected ? '✨ Ready to assist you' : '🔄 Connecting to VEGA...'}
              </p>
            </div>
          </div>
        </div>

        {/* Chat Interface */}
        <div className="flex-1 max-w-2xl">
          <div className="h-full bg-black/30 backdrop-blur-lg rounded-3xl border border-white/20 shadow-2xl overflow-hidden flex flex-col">

            {/* Chat Header */}
            <div className="flex justify-between items-center p-6 border-b border-white/20 bg-white/5">
              <h3 className="text-xl font-semibold text-cyan-400">💬 Conversation</h3>
              <button 
                onClick={() => setMessages([])}
                className="px-4 py-2 bg-white/20 hover:bg-white/30 rounded-xl border border-white/20 text-white/90 transition-all duration-200"
              >
                🗑️ Clear
              </button>
            </div>

            {/* Messages */}
            <ChatMessages messages={messages} />

            {/* Text Input */}
            <div className="p-4 border-t border-white/20 bg-white/5">
              <div className="flex gap-3">
                <input
                  type="text"
                  value={textInput}
                  onChange={(e) => setTextInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && sendTextCommand()}
                  placeholder="Type your command here..."
                  disabled={!isConnected}
                  className="flex-1 bg-white/20 border border-white/30 rounded-2xl px-6 py-3 text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-cyan-400 focus:border-transparent backdrop-blur-sm"
                />
                <button 
                  onClick={sendTextCommand}
                  disabled={!isConnected || !textInput.trim()}
                  className="w-12 h-12 bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-400 hover:to-blue-400 rounded-xl text-white font-bold disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200"
                >
                  📤
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Initialize Button */}
      <motion.button
        className="fixed bottom-8 right-8 px-6 py-3 bg-gradient-to-r from-purple-600 to-cyan-600 hover:from-purple-500 hover:to-cyan-500 rounded-2xl text-white font-semibold shadow-2xl backdrop-blur-lg border border-white/20 transition-all duration-300"
        onClick={initializeVega}
        disabled={!isConnected}
        whileHover={{ scale: 1.05, y: -2 }}
        whileTap={{ scale: 0.95 }}
      >
        🚀 Initialize VEGA
      </motion.button>
    </div>
  );
}

// Voice Visualizer Component with Advanced Animations
function VoiceVisualizer({ isListening, isSpeaking, isConnected }) {
  return (
    <div className="relative w-80 h-80 flex items-center justify-center">

      {/* Outer Wave Rings */}
      {(isListening || isSpeaking) && (
        <>
          {[...Array(4)].map((_, i) => (
            <motion.div
              key={i}
              className={`absolute rounded-full border-2 ${
                isSpeaking 
                  ? 'border-blue-400/40' 
                  : 'border-green-400/40'
              }`}
              style={{
                width: 200 + i * 40,
                height: 200 + i * 40,
              }}
              animate={{
                scale: [1, 1.1, 1],
                opacity: [0.3, 0.6, 0.3],
                rotate: i % 2 === 0 ? 360 : -360,
              }}
              transition={{
                duration: 2 + i * 0.5,
                repeat: Infinity,
                ease: "easeInOut",
              }}
            />
          ))}
        </>
      )}

      {/* Central Core */}
      <motion.div
        className={`
          relative w-40 h-40 rounded-full flex items-center justify-center text-4xl
          backdrop-blur-lg border-2 shadow-2xl
          ${isSpeaking 
            ? 'bg-blue-500/40 border-blue-400 shadow-blue-500/50' 
            : isListening 
            ? 'bg-green-500/40 border-green-400 shadow-green-500/50'
            : 'bg-white/20 border-white/30 shadow-white/20'
          }
        `}
        animate={{
          scale: isSpeaking ? [1, 1.2, 1] : isListening ? [1, 1.1, 1] : 1,
        }}
        transition={{
          duration: 1.5,
          repeat: (isListening || isSpeaking) ? Infinity : 0,
          ease: "easeInOut",
        }}
      >
        <motion.div
          animate={{
            rotate: isConnected ? 360 : 0,
          }}
          transition={{
            duration: 10,
            repeat: isConnected ? Infinity : 0,
            ease: "linear",
          }}
        >
          {isSpeaking ? '🔊' : isListening ? '🎤' : '🤖'}
        </motion.div>
      </motion.div>

      {/* Sound Wave Lines */}
      {isSpeaking && (
        <div className="absolute inset-0 flex items-center justify-center">
          {[...Array(6)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-1 bg-gradient-to-t from-blue-400 to-cyan-400 rounded-full"
              style={{
                left: `${45 + i * 5}%`,
                height: 20 + i * 10,
              }}
              animate={{
                scaleY: [0.5, 2, 0.5],
              }}
              transition={{
                duration: 0.8,
                repeat: Infinity,
                delay: i * 0.1,
                ease: "easeInOut",
              }}
            />
          ))}
        </div>
      )}
    </div>
  );
}

// Chat Messages Component
function ChatMessages({ messages }) {
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="flex-1 overflow-y-auto p-4 space-y-4 scrollbar-thin scrollbar-thumb-white/20">
      <AnimatePresence>
        {messages.map((message) => (
          <motion.div
            key={message.id}
            className={`
              p-4 rounded-2xl max-w-xs backdrop-blur-sm border
              ${message.type === 'user' 
                ? 'ml-auto bg-green-500/20 border-green-400/30 text-green-100' 
                : message.type === 'assistant' 
                ? 'mr-auto bg-blue-500/20 border-blue-400/30 text-blue-100'
                : 'mx-auto text-center bg-white/10 border-white/20 text-white/90'
              }
            `}
            initial={{ opacity: 0, y: 20, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.3, type: "spring" }}
          >
            <div className="flex justify-between items-center mb-2 text-xs opacity-70">
              <span>
                {message.type === 'user' ? '👤 You' : 
                 message.type === 'assistant' ? '🤖 VEGA' : '⚙️ System'}
              </span>
              <span>{new Date(message.timestamp).toLocaleTimeString()}</span>
            </div>
            <div className="text-sm leading-relaxed">{message.text}</div>
          </motion.div>
        ))}
      </AnimatePresence>
      <div ref={messagesEndRef} />
    </div>
  );
}

// Animated Background Component
function AnimatedBackground() {
  return (
    <div className="absolute inset-0">
      {/* Floating Particles */}
      {[...Array(20)].map((_, i) => (
        <motion.div
          key={i}
          className="absolute w-2 h-2 bg-white/20 rounded-full"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
          }}
          animate={{
            y: [-20, -100, -20],
            opacity: [0, 1, 0],
          }}
          transition={{
            duration: 3 + Math.random() * 2,
            repeat: Infinity,
            delay: Math.random() * 2,
          }}
        />
      ))}

      {/* Grid Pattern */}
      <div 
        className="absolute inset-0 opacity-10"
        style={{
          backgroundImage: `
            linear-gradient(rgba(59, 130, 246, 0.3) 1px, transparent 1px),
            linear-gradient(90deg, rgba(59, 130, 246, 0.3) 1px, transparent 1px)
          `,
          backgroundSize: '50px 50px'
        }}
      />
    </div>
  );
}

export default App;
