import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';

const BACKEND_URL = window.location.origin;

function App() {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [messages, setMessages] = useState([]);
  const [textInput, setTextInput] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  useEffect(() => {
    const newSocket = io(BACKEND_URL, {
      transports: ['websocket', 'polling'],
      timeout: 10000,
    });

    // Connection handlers
    newSocket.on('connect', () => {
      console.log('‚úÖ Connected to JARVIS');
      setIsConnected(true);
      setConnectionStatus('Connected');
      addMessage('system', 'ü§ñ JARVIS Web Interface Connected! Click microphone to start or use text input below.');
    });

    newSocket.on('disconnect', () => {
      console.log('‚ùå Disconnected');
      setIsConnected(false);
      setIsListening(false);
      setIsSpeaking(false);
      setConnectionStatus('Disconnected');
    });

    // Voice interaction handlers
    newSocket.on('listening_started', () => {
      setIsListening(true);
      addMessage('system', 'üé§ Listening...');
    });

    newSocket.on('listening_stopped', () => {
      setIsListening(false);
    });

    newSocket.on('user_spoke', (data) => {
      addMessage('user', data.text, data.timestamp);
    });

    newSocket.on('assistant_speaking', (data) => {
      addMessage('assistant', data.text, data.timestamp);
      setIsSpeaking(true);
    });

    newSocket.on('assistant_finished_speaking', () => {
      setIsSpeaking(false);
    });

    newSocket.on('command_executed', (data) => {
      console.log('Command executed:', data);
    });

    newSocket.on('error', (data) => {
      addMessage('system', `‚ùå Error: ${data.message}`);
      setIsListening(false);
      setIsSpeaking(false);
    });

    newSocket.on('no_speech_detected', () => {
      addMessage('system', '‚ö†Ô∏è No speech detected. Try again.');
      setIsListening(false);
    });

    setSocket(newSocket);

    return () => newSocket.close();
  }, []);

  const addMessage = (type, text, timestamp = null) => {
    const message = {
      type,
      text,
      timestamp: timestamp || new Date().toISOString(),
      id: Date.now() + Math.random()
    };
    setMessages(prev => [...prev, message]);
  };

  const toggleListening = () => {
    if (!socket || !isConnected) return;

    if (isListening) {
      socket.emit('stop_listening');
    } else {
      socket.emit('start_listening');
    }
  };

  const sendTextCommand = () => {
    if (!textInput.trim() || !socket || !isConnected) return;

    socket.emit('send_text_command', { command: textInput });
    setTextInput('');
  };

  const initializeJarvis = () => {
    if (socket && isConnected) {
      socket.emit('initialize_jarvis');
    }
  };

  return (
    <div className="app">
      {/* Background Animation */}
      <div className="background">
        <div className="stars"></div>
        <div className="grid"></div>
      </div>

      {/* Header */}
      <header className="header">
        <motion.h1
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          J.A.R.V.I.S
        </motion.h1>
        <motion.p
          initial={{ y: -30, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          Just A Rather Very Intelligent System
        </motion.p>
        <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
          {isConnected ? 'üü¢' : 'üî¥'} {connectionStatus}
        </div>
      </header>

      {/* Main Interface */}
      <main className="main">
        <div className="interface-container">
          {/* Voice Visualizer */}
          <div className="visualizer-section">
            <VoiceVisualizer isListening={isListening} isSpeaking={isSpeaking} />

            <div className="controls">
              <motion.button
                className={`mic-button ${isListening ? 'listening' : ''} ${isSpeaking ? 'speaking' : ''}`}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={toggleListening}
                disabled={!isConnected || isSpeaking}
              >
                {isListening ? 'üõë' : 'üé§'}
              </motion.button>

              <div className="status">
                {isSpeaking ? 'ü§ñ JARVIS is responding...' :
                 isListening ? 'üé§ Listening for commands...' :
                 isConnected ? '‚ú® Ready to assist' : 'üîÑ Connecting...'}
              </div>
            </div>
          </div>

          {/* Chat Interface */}
          <div className="chat-section">
            <div className="chat-header">
              <h3>üí¨ Live Conversation</h3>
              <button onClick={() => setMessages([])} className="clear-btn">
                üóëÔ∏è Clear
              </button>
            </div>

            <ChatMessages messages={messages} />

            {/* Text Input */}
            <div className="text-input-section">
              <input
                type="text"
                value={textInput}
                onChange={(e) => setTextInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendTextCommand()}
                placeholder="Type a command or question..."
                disabled={!isConnected}
                className="text-input"
              />
              <button 
                onClick={sendTextCommand}
                disabled={!isConnected || !textInput.trim()}
                className="send-btn"
              >
                üì§
              </button>
            </div>
          </div>
        </div>

        {/* Initialize Button */}
        <motion.button
          className="init-button"
          onClick={initializeJarvis}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          disabled={!isConnected}
        >
          üöÄ Initialize JARVIS
        </motion.button>
      </main>
    </div>
  );
}

// Voice Visualizer Component
function VoiceVisualizer({ isListening, isSpeaking }) {
  return (
    <div className="voice-visualizer">
      <motion.div
        className="visualizer-core"
        animate={{
          scale: isSpeaking ? [1, 1.2, 1] : isListening ? [1, 1.1, 1] : 1,
          boxShadow: isSpeaking 
            ? ['0 0 20px #0096ff', '0 0 40px #0096ff', '0 0 20px #0096ff']
            : isListening 
            ? ['0 0 20px #00ff00', '0 0 30px #00ff00', '0 0 20px #00ff00']
            : '0 0 10px #333'
        }}
        transition={{
          duration: 1.5,
          repeat: (isListening || isSpeaking) ? Infinity : 0,
        }}
      >
        <div className="visualizer-icon">
          {isSpeaking ? 'üîä' : isListening ? 'üé§' : 'ü§ñ'}
        </div>
      </motion.div>

      {/* Animated Rings */}
      {(isListening || isSpeaking) && (
        <>
          <motion.div
            className="visualizer-ring ring-1"
            animate={{ rotate: 360, scale: [1, 1.1, 1] }}
            transition={{ duration: 3, repeat: Infinity, ease: "linear" }}
          />
          <motion.div
            className="visualizer-ring ring-2"
            animate={{ rotate: -360, scale: [1, 1.05, 1] }}
            transition={{ duration: 4, repeat: Infinity, ease: "linear" }}
          />
        </>
      )}
    </div>
  );
}

// Chat Messages Component
function ChatMessages({ messages }) {
  const messagesEndRef = React.useRef(null);

  React.useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="chat-messages">
      <AnimatePresence>
        {messages.map((message) => (
          <motion.div
            key={message.id}
            className={`message ${message.type}`}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.3 }}
          >
            <div className="message-header">
              <span className="sender">
                {message.type === 'user' ? 'üë§ You' : 
                 message.type === 'assistant' ? 'ü§ñ JARVIS' : '‚öôÔ∏è System'}
              </span>
              <span className="timestamp">
                {new Date(message.timestamp).toLocaleTimeString()}
              </span>
            </div>
            <div className="message-text">{message.text}</div>
          </motion.div>
        ))}
      </AnimatePresence>
      <div ref={messagesEndRef} />
    </div>
  );
}

export default App;
