import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import io from 'socket.io-client';
import './App.css'; // We'll keep minimal CSS for custom animations, but use Tailwind primarily

const BACKEND_URL = process.env.NODE_ENV === 'production' 
  ? window.location.origin 
  : 'http://localhost:5000';

function App() {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [messages, setMessages] = useState([]);
  const [textInput, setTextInput] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  useEffect(() => {
    const newSocket = io(BACKEND_URL, {
      transports: ['websocket', 'polling'],
      timeout: 10000,
    });

    // Connection handlers
    newSocket.on('connect', () => {
      console.log('✅ Connected to VEGA');
      setIsConnected(true);
      setConnectionStatus('Connected');
      addMessage('system', '🤖 VEGA Web Interface Connected! Click microphone to start or use text input below.');
    });

    newSocket.on('disconnect', () => {
      console.log('❌ Disconnected');
      setIsConnected(false);
      setIsListening(false);
      setIsSpeaking(false);
      setConnectionStatus('Disconnected');
    });

    // Voice interaction handlers
    newSocket.on('listening_started', () => {
      setIsListening(true);
      addMessage('system', '🎤 Listening...');
    });

    newSocket.on('listening_stopped', () => {
      setIsListening(false);
    });

    newSocket.on('user_spoke', (data) => {
      addMessage('user', data.text, data.timestamp);
    });

    newSocket.on('assistant_speaking', (data) => {
      addMessage('assistant', data.text, data.timestamp);
      setIsSpeaking(true);
    });

    newSocket.on('assistant_finished_speaking', () => {
      setIsSpeaking(false);
    });

    newSocket.on('command_executed', (data) => {
      console.log('Command executed:', data);
    });

    newSocket.on('error', (data) => {
      addMessage('system', `❌ Error: ${data.message}`);
      setIsListening(false);
      setIsSpeaking(false);
    });

    newSocket.on('no_speech_detected', () => {
      addMessage('system', '⚠️ No speech detected. Try again.');
      setIsListening(false);
    });

    setSocket(newSocket);

    return () => newSocket.close();
  }, []);

  const addMessage = (type, text, timestamp = null) => {
    const message = {
      type,
      text,
      timestamp: timestamp || new Date().toISOString(),
      id: Date.now() + Math.random()
    };
    setMessages(prev => [...prev, message]);
  };

  const toggleListening = () => {
    if (!socket || !isConnected) return;

    if (isListening) {
      socket.emit('stop_listening');
    } else {
      socket.emit('start_listening');
    }
  };

  const sendTextCommand = () => {
    if (!textInput.trim() || !socket || !isConnected) return;

    socket.emit('send_text_command', { command: textInput });
    setTextInput('');
  };

  const initializeVega = () => {
    if (socket && isConnected) {
      socket.emit('initialize_vega');
    }
  };

  return (
    <div className="min-h-screen bg-purple-900 relative overflow-hidden">
      {/* Background Waves - Custom CSS for seamless purple wave background */}
      <div className="fixed inset-0 bg-gradient-to-br from-purple-800 via-purple-900 to-indigo-900 z-0">
        <div className="absolute inset-0 opacity-20">
          <div className="wave-bg"></div>
        </div>
      </div>

      {/* Header - Simple name at top */}
      <header className="relative z-10 text-center pt-8 pb-4">
        <motion.h1
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8 }}
          className="text-6xl font-bold bg-gradient-to-r from-purple-300 to-pink-400 bg-clip-text text-transparent"
        >
          VEGA
        </motion.h1>
        <div className={`inline-block mt-2 px-4 py-2 rounded-full text-sm font-medium border ${
          isConnected 
            ? 'bg-green-500/20 border-green-400 text-green-300' 
            : 'bg-red-500/20 border-red-400 text-red-300'
        }`}>
          {isConnected ? '🟢' : '🔴'} {connectionStatus}
        </div>
      </header>

      {/* Main Interface - Assistant UI covering most space */}
      <main className="relative z-10 flex flex-col items-center justify-center h-screen px-4">
        {/* Voice Visualizer - Large central mic with waves */}
        <div className="flex flex-col items-center justify-center flex-1 w-full max-w-md">
          <VoiceVisualizer isListening={isListening} isSpeaking={isSpeaking} />

          {/* Mic Button Below Visualizer */}
          <motion.button
            className={`relative mt-8 w-20 h-20 rounded-full border-4 flex items-center justify-center text-2xl font-bold transition-all duration-300 ${
              isListening 
                ? 'border-green-400 bg-green-500/20 animate-pulse' 
                : isSpeaking 
                ? 'border-blue-400 bg-blue-500/20 animate-glow' 
                : 'border-purple-400 bg-purple-500/20 hover:scale-110'
            } disabled:opacity-50 disabled:cursor-not-allowed`}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={toggleListening}
            disabled={!isConnected || isSpeaking}
          >
            {isListening ? '🛑' : '🎤'}
          </motion.button>

          <div className="mt-4 text-center text-purple-200 text-lg">
            {isSpeaking ? '🤖 VEGA is responding...' :
             isListening ? '🎤 Listening for commands...' :
             isConnected ? '✨ Ready to assist' : '🔄 Connecting...'}
          </div>
        </div>

        {/* Small Aesthetic Chat Window - Bottom, blended with background */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 w-full max-w-md z-20">
          <div className="bg-white/10 backdrop-blur-xl rounded-2xl border border-white/20 p-4 shadow-2xl">
            <div className="flex justify-between items-center mb-3">
              <h3 className="text-purple-300 font-semibold text-sm">💬 Conversation</h3>
              <button 
                onClick={() => setMessages([])} 
                className="text-purple-400 hover:text-purple-200 text-sm"
              >
                🗑️ Clear
              </button>
            </div>

            <ChatMessages messages={messages} />

            {/* Text Input */}
            <div className="flex gap-2 mt-3">
              <input
                type="text"
                value={textInput}
                onChange={(e) => setTextInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendTextCommand()}
                placeholder="Type a command..."
                disabled={!isConnected}
                className="flex-1 bg-white/10 border border-white/20 rounded-xl px-3 py-2 text-white placeholder-purple-300 focus:outline-none focus:border-purple-400 transition-colors"
              />
              <motion.button 
                onClick={sendTextCommand}
                disabled={!isConnected || !textInput.trim()}
                className="w-10 h-10 rounded-xl bg-purple-500/20 border border-purple-400 text-purple-300 flex items-center justify-center hover:bg-purple-500/30 disabled:opacity-50"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                📤
              </motion.button>
            </div>
          </div>
        </div>

        {/* Initialize Button - Small, bottom right */}
        <motion.button
          className="absolute bottom-8 right-8 bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-3 rounded-xl font-semibold shadow-lg hover:shadow-purple-500/50 disabled:opacity-50"
          onClick={initializeVega}
          whileHover={{ scale: 1.05, y: -2 }}
          whileTap={{ scale: 0.95 }}
          disabled={!isConnected}
        >
          🚀 Initialize VEGA
        </motion.button>
      </main>
    </div>
  );
}

// Voice Visualizer Component - Purple waves around mic, animated on speak/listen
function VoiceVisualizer({ isListening, isSpeaking }) {
  const isActive = isListening || isSpeaking;

  return (
    <div className="relative w-64 h-64 flex items-center justify-center">
      {/* Central Mic Icon */}
      <motion.div
        className="relative z-10 w-24 h-24 bg-purple-600/20 rounded-full border-2 border-purple-400 flex items-center justify-center"
        animate={{
          scale: isActive ? [1, 1.2, 1] : 1,
          boxShadow: isActive 
            ? ['0 0 20px rgba(147, 51, 234, 0.5)', '0 0 40px rgba(147, 51, 234, 0.8)', '0 0 20px rgba(147, 51, 234, 0.5)']
            : '0 0 10px rgba(147, 51, 234, 0.3)'
        }}
        transition={{
          duration: isSpeaking ? 0.8 : 1.5,
          repeat: isActive ? Infinity : 0,
          ease: 'easeInOut'
        }}
      >
        <div className="text-3xl">🎤</div>
      </motion.div>

      {/* Animated Waves - Multiple rings expanding/moving when active */}
      <AnimatePresence>
        {isActive && (
          <>
            {[...Array(4)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute rounded-full border border-purple-400/50"
                initial={{ scale: 1, opacity: 1 }}
                animate={{
                  scale: [1, 2 + i * 0.5],
                  opacity: [1, 0],
                  rotate: isSpeaking ? 360 : 0
                }}
                transition={{
                  duration: 2 + i * 0.5,
                  repeat: Infinity,
                  delay: i * 0.2,
                  ease: 'linear'
                }}
                style={{
                  width: `${200 + i * 50}px`,
                  height: `${200 + i * 50}px`,
                  borderColor: isSpeaking ? 'rgba(168, 85, 247, 0.6)' : 'rgba(139, 92, 246, 0.4)'
                }}
              />
            ))}
          </>
        )}
      </AnimatePresence>
    </div>
  );
}

// Chat Messages Component
function ChatMessages({ messages }) {
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="chat-messages max-h-48 overflow-y-auto space-y-2 mb-3">
      <AnimatePresence>
        {messages.map((message) => (
          <motion.div
            key={message.id}
            className={`p-3 rounded-xl text-sm ${
              message.type === 'user' 
                ? 'bg-green-500/20 border-l-4 border-green-400 ml-2' 
                : message.type === 'assistant' 
                ? 'bg-blue-500/20 border-l-4 border-blue-400 mr-2' 
                : 'bg-gray-500/20 border-l-4 border-gray-400 text-center mx-2'
            }`}
            initial={{ opacity: 0, y: 20, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.3 }}
          >
            <div className="flex justify-between text-xs text-purple-300 mb-1 opacity-75">
              <span>
                {message.type === 'user' ? '👤 You' : 
                 message.type === 'assistant' ? '🤖 VEGA' : '⚙️ System'}
              </span>
              <span>{new Date(message.timestamp).toLocaleTimeString()}</span>
            </div>
            <div className="text-white">{message.text}</div>
          </motion.div>
        ))}
      </AnimatePresence>
      <div ref={messagesEndRef} />
    </div>
  );
}

export default App;
